#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,D,e; vec3 p,r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p=vec3(log(D=length(p.xy)*2.),p.y,atan(p.x,p.z));
    p.xy*=R2D(iTime*.5);

    for(j=0.;j++<5.;)
        p=abs(abs(p)-.05)-.04,       
        d=max(max(p.z,p.x),p.y),
        d-=D/2e4;

    e+=d;
}o+=vec4(exp(p*p),1)*5./i;
}
