////////////////////////////////////////////////////////////////////////////////////////////////////////////// scene intersection
//==========================================================================================================//
void Intersect_Ray_Cube(vec3 rp, vec3 rd, vec3 c0p, vec3 c0d, 
                        inout float hit, inout float t, inout vec3 n)
{
    vec2 tt; vec3 n0, n1;
    float th = Intersect_Ray_Cube(rp, rd, c0p, c0d, /*out:*/ tt, n0, n1);

    if(th > 0.0)
    if(hit <= 0.0 || tt.x < t)
    {
        t = tt.x;
        n = n0;

        hit = 1.0;
    }        
}

bool Intersect_Ray_Plate(vec3 rp, vec3 rd, vec3 c0p, vec3 c0d, float c1p_y, float c1dxz, float c1dy, float c1ps,
                        inout float hit, inout float t, inout vec3 n)
{
    vec3 c1p = vec3(0.0, c1p_y, 0.0);
    vec3 c1d = vec3(c1dxz, c1dy, c1dxz);

    //c1p.y = 0.0;// remove tile pattern

    c1p.xz = (floor(rp.xz*c1ps+0.5))/c1ps;

    bool isInsideC0 = IsInsideCube(rp, c0p, c0d);
    bool isInsideC1 = IsInsideCube(rp, c1p, c1d);

    if(isInsideC0 && isInsideC1)
    {
        float t0; vec3 n0;
        Intersect_Ray_CubeBackside(rp, rd, c1p, c1d, t0, n0);

        if(IsInsideCube(rp+rd*t0, c0p, c0d))
        {
            if(hit <= 0.0 || t0 < t)
            {
                hit = 1.0;
                t = t0;
                n = n0;

                return true;
            }
        }
    }
    else
    {
        vec2 tt; vec3 n0, n1;
        float th = Intersect_Ray_Cube(rp, rd, c0p, c0d, /*out:*/ tt, n0, n1);

        bool hit0 = th > 0.0;
        if ( hit0 )
        {
            c1p.xz = (floor((rp.xz+rd.xz*tt.x)*c1ps+0.5))/c1ps;

            if(IsInsideCube(rp+rd*tt.x, c1p, c1d))
            {
                float t0; vec3 n0;
                Intersect_Ray_CubeBackside(rp, rd, c1p, c1d, t0, n0);

                if(IsInsideCube(rp+rd*t0, c0p, c0d))
                {
                    if(hit <= 0.0 || t0 < t)
                    {
                        hit = 1.0;
                        t = t0;
                        n = n0;

                        return true;
                    }
                }
            }
            else
            {
                if(hit <= 0.0 || tt.x < t)
                {
                    hit = 1.0;
                    t = tt.x;
                    n = n0;

                    return true;
                }
            }

        }        
    }

    return false;
}

vec3 TransA(vec3 u, bool foo)
{
    if(foo)
    {
        u.xy = u.yx;
        u.x *= -1.0;
    }
    else
    {
        u.x *= -1.0;
        u.xy = u.yx;
    }

    return u;
}

vec3 TransB(vec3 u, bool foo)
{
    if(foo)
    {
        u.zy = u.yz;
        u.z *= -1.0;
    }
    else
    {
        u.z *= -1.0;
        u.zy = u.yz;
    }

    return u;
}


void Intersect_Ray_Plates(vec3 rp, vec3 rd, vec3 c0p, vec3 c0d, float c1p_y, float c1dxz, float c1dy, float c1ps,
                         inout float hit, inout float t, inout vec3 n)
{
    rp.x += exp2(-18.0);
    //if(false)
    if(Intersect_Ray_Plate(TransA(rp, false), TransA(rd, false), c0p, c0d, c1p_y, c1dxz, c1dy, c1ps, /*inout*/ hit, t, n))
    {
        n = TransA(n, true);
    }
    //if(false)
    if(Intersect_Ray_Plate(TransB(rp, false), TransB(rd, false), c0p, c0d, c1p_y, c1dxz, c1dy, c1ps, /*inout*/ hit, t, n))
    {
        n = TransB(n, true);
    }    
}

float Intersect_Scene(
vec3 rp, vec3 rd,
out float t, out vec3 n, out vec3 a)
{
    float hit = 0.0;

    float c1dxz = 0.4;
    float c1ps = 0.85;

