#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//formula for creating colors;
#define H(h)  (  cos(  h*2. + vec3(1,2,0)   )*.6 + .2 )

#define H2(h)  (  cos(  h + vec3(1,2,0)   )*.5 + .1 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*600.;

    float sc,dotp,totdist=0., t=0.;

    float tt = mod(iTime,5.);

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(0,0,-1.-exp(tt*2.) );
        p.xz *= rot( .6*sin(iTime) );
        p.xy *= rot( tt*2.+sin(tt/2.));
        p.yzw = p.xyz;
        sc = 1.; 

        for (float j=0.; j<10.; j++) {

            p = abs(p)*.7;
            dotp = min(2.,max(1./dot(p.xz,p.xz),.1));
            sc *= dotp;
            p = p * dotp - .1; 

        }

        float dist = abs( length(p)-.2 )/sc ;
        float stepsize = dist/60. ;     
        totdist += stepsize;

        c +=
             .04 * H2(  atan( p.w, p.z ) ) * exp(-totdist*totdist/100.)
           + .02 * H(M(sc)) *  exp(-i*i*stepsize*stepsize);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );


}
