 void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 o_scl_screen = iResolution.xy;
    vec2 o_trn_pixel = fragCoord.xy;

    vec2 o_trn_nor_pix = (fragCoord.xy - o_scl_screen.xy*.5)
        /o_scl_screen.yy;

    o_trn_nor_pix *= 2.+sin(iTime*.19)*6.;
    vec2 o_trn_nor_pix2 = o_trn_nor_pix*vec2(10.,10.);
    o_trn_nor_pix2 += .5;

    vec2 o_trn_nor_pix2_flr = floor(o_trn_nor_pix2);
    o_trn_nor_pix2 = fract(o_trn_nor_pix2)-.5;
    o_trn_nor_pix2 *= 20.;

    float n_tau = radians(360.);
    float nang = (atan(o_trn_nor_pix.x, o_trn_nor_pix.y)/n_tau);
    float nang2 = (atan(o_trn_nor_pix2_flr.x, o_trn_nor_pix2_flr.y)/n_tau);
    nang2 = sin(nang2*n_tau*4.)*.5+.5;
    float ndcntr2 = length(o_trn_nor_pix2_flr);

    float nt = iTime*2.;
    vec3 o_t = vec3(
        iTime*2.+nang2*1.,
        iTime*2.+nang2*2.,
        iTime*2.+nang2*3.
    );

    float ncirc = length(o_trn_nor_pix2);
    float nsqr = max(abs(o_trn_nor_pix2.x), abs(o_trn_nor_pix2.y));
    float nintrpl = 0.1;

    float nradius = (sin(nang2+nt+ndcntr2*1.2)*.5+.5)*.5;
    vec3 oradius = vec3(
        (sin(nang2+o_t.x+ndcntr2*1.2)*.5+.5)*.5,
        (sin(nang2+o_t.y+ndcntr2*1.2)*.5+.5)*.5,
        (sin(nang2+o_t.z+ndcntr2*1.2)*.5+.5)*.5
    );

    vec3 ointpl = vec3(oradius*2.);

    nintrpl = nradius*2.;
    nradius -= 0.1;

    float n = nintrpl*ncirc+(1.-nintrpl)*nsqr;
    vec3 on = vec3(
        ointpl.x*ncirc+(1.-ointpl.x)*nsqr,
        ointpl.y*ncirc+(1.-ointpl.y)*nsqr,
        ointpl.z*ncirc+(1.-ointpl.z)*nsqr
    );

    on = abs(on-oradius);
    on = vec3(
        1.-pow(on.x, 1./3.),
        1.-pow(on.y, 1./3.),
        1.-pow(on.z, 1./3.)
    );

    n = abs(n-nradius);
    n = pow(n, 1./3.);

    vec3 o_col = vec3(on.x, on.y, on.z);

    fragColor = vec4(o_col, 1.0);
}
