void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5;
    vec2 offset = vec2(sin(iTime*2.)*0.5, cos(iTime*2.)*0.5);
    uv.x -= offset.x;
    uv.y -= offset.y;

    // Time varying pixel color
    //if(length(uv.x*uv.x+uv.y*uv.y)<0.02) col=vec3(1,1,1);
    vec3 col =0.5+0.7*cos(iTime+uv.x*uv.x+uv.y*uv.y+vec3(3,5,8));
    // Output to screen
    fragColor = vec4(col,1.0);
}
