void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    float time = 25. * sin(iTime/25.);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1.0);
    col.x = (sin(time) + 1.) / 2.;
    col.y = (cos(time) + 1.) / 2.;


    if (sin(time * uv.y) >= cos(time * uv.x))
    {
        col = vec3(1.0) - col;
    }

    // make colors a little easier on the eyes
    //col *= .85;

    // Output to screen
    fragColor = vec4(col,1.0);
}
