#define R iResolution.xy

float chr(vec2 p, int c) {
    if (c == 32) return 0.0;
    p = (((p-vec2(0, 1))+vec2(float(int(1.0+float(c-97))),
    int(-6 - (c > 111 ? 1 : 0))))/16.);
    return texture(iChannel0, p).x;
}


#define TEXT(P, CHARS)\
{\
  int chars[] = int[] CHARS;\
  int len = chars.length();\
  vec2 id = floor((P)*(float(len/2)+1.));\
  vec2 lv = fract((P)*(float(len/2)+1.));\
  int idx = int(id.x);\
  col += (idx < 0 || idx >= len || abs(lv.x-0.9)<0.1 || abs(lv.y-0.9)<0.1) ? 0.0 : \
      chr(lv, chars[idx]);\
}

void mainImage(out vec4 o, in vec2 fc) {
  vec3 col = vec3(0.0);
  vec2 uv = fc.xy/R.y;
  uv.y -= 0.15*iTime;
  TEXT(uv, (115, 104, 97, 100, 101, 114, 116, 111, 121))
  o = vec4(col, 1.0);
}
