#define R iResolution.xy
void mainImage( out vec4 O, in vec2 U )
{
    U = (2.*U-R)/R.y*6.;
    vec2 cc = func01(iTime);
    float c1 = exp( -length( U-.6*cc)*2.);
    O =  vec4( c1 * vec2(cc*cc),0,0) ;
}

vec2 mainSound( int samp, float time )
{
    return func01(time);
}

#define tau 6.2831
#define S(f) sin(tau*time*f)
#define A 2.85  //play with this >2.85 is noisier, 4 is total chaos
#define F(x,a) a*x*(1.-x) //Verhulst logistic function

vec2 func01(float time) {

    float R=0.,
          i=0.,x=.7;

    for (;i<30.;i++) {
        x = F(x,A);
        R += S( 140.*x );
    }

    return S(1./10./tau+S(1./5./tau))     //envelope
            *vec2(R)                      //waveform
            *vec2(cos(time),sin(time));   //pan

}
