#define STEPS 16.

float haiba(vec2 uv) {
	uv 			= uv * 4.0;
    uv.x 		+= 1.5;
    float x 	= 1.0;
    float w 	= 0.7;
    float cp 	= w;

    if (abs(cp - w - uv.x) < w) { 
        vec2 _uv	= uv - vec2(cp - (w * .5), 0.);

        // -- H --   
        _uv.y 		-=.1;

        _uv.x 		= abs(_uv.x);

        float _x 	= length(vec2(max(0., abs(_uv.x) - .25), _uv.y));

        float _f 	= min(_x, length(vec2(_uv.x - .25, max(0., abs(_uv.y) - .5))));

        return _f;
    }

    cp 			+= w;

    if (abs(cp - w - uv.x) < w) { 
        vec2 _uv 	= uv - vec2(cp - (w * .5), 0.);

        // -- a --   
        _uv 		= -_uv;

        float _x 	= abs(length(vec2(max(0., abs(_uv.x) - .05), _uv.y - .2)) - .2);            

        _x 			= min(_x, length(vec2(_uv.x + .25, max(0., abs(_uv.y - .2) - .2))));

        float _f 	= min(_x, (_uv.x < 0.? _uv.y <0. : atan(_uv.x, _uv.y + 0.15) > 2.) ? 
                       abs(length(vec2(_uv.x, max(0., abs(_uv.y) - .15))) - .25) : 
                       length(vec2(_uv.x - .22734, _uv.y + .254)));

        return _f;
    }

    w 			= 0.3;

    cp 			+= w;

    if (abs(cp - w - uv.x) < w) { 
        vec2 _uv 	= uv - vec2(cp - (w * .5), 0.);

        // -- i --

        float _f 	= min(length(vec2(_uv.x, max(0., abs(_uv.y) - .4))), length(vec2(_uv.x, _uv.y - .6)));

        return _f;
    }

    w 			= 0.7;

    cp 			+= w;

    if (abs(cp - w - uv.x) < w) { 
        vec2 _uv 	= uv - vec2(cp - (w * .5), 0.);

        // -- b --

        float _x 	= abs(length(vec2(_uv.x, max(0., abs(_uv.y) - .15))) - .25);
        _uv.x 		+= .25;
        _uv.y 		-= .2;
        float _s 	= length(vec2(_uv.x, max(0., abs(_uv.y) - .6)));
        float _f 	= min(_x, _s);

        return _f;
    }

    cp 			+= w;

    if (abs(cp - w - uv.x) < w) { 
        vec2 _uv 	= uv - vec2(cp - (w * .5), 0.);

        // -- a --   
        _uv 		= -_uv;

        float _x 	= abs(length(vec2(max(0., abs(_uv.x) - .05), _uv.y - .2)) - .2);            

        _x 			= min(_x, length(vec2(_uv.x + .25, max(0., abs(_uv.y - .2) - .2))));

        float _f 	= min(_x, (_uv.x < 0.? _uv.y <0. : atan(_uv.x, _uv.y + 0.15) > 2.) ? 
                       abs(length(vec2(_uv.x, max(0., abs(_uv.y) - .15))) - .25) : 
                       length(vec2(_uv.x - .22734, _uv.y + .254)));

        return _f;
    }

    return 1.0;
}

float scene (in vec2 p) {
	float f 	= haiba(p) * 6.5;

	return exp(-.5*f)/(f + 1.);
}

float scene_s(in vec2 uv) {
	return clamp(100. * scene(uv) - 50., 1., 10.);
}

float spec(in vec2 uv, in vec2 m) {
    vec2  e     = 1. / iResolution.xy;
    float f     = scene_s(uv);
    vec2  n     = vec2(scene_s(uv - vec2(e.x, 0)) - f, scene_s(uv - vec2(0, e.y)) - f);

    vec2  l     = m - uv;
    float d     = length(l);
    float ndl   = dot(l / d, normalize(n));

    float s     = clamp(ndl * clamp(1. - d, 0., 1.), 0., 1.);
	return s * s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv 	= (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 m 		= (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;

    vec2 o 		= uv;
    vec2 dr 	= (m - uv) / STEPS;

    for (float i = 0.; i < 1.; i += 0.5/STEPS) {
        float f = scene (o);
        f 		= .175 * pow((0.975 + f * f), -10.);
        o 		+= f * dr;
    }

    o 			-= m;

    vec3 col 	= vec3(0.);
    vec3 lcol 	= vec3(0.15); 

    float d 	= dot(o, o);
	vec3 sun 	= .1 * lcol * exp(2. / (2.5 * d + 4. * lcol));    
    col 		+= sun;

	float f 	= clamp(100. * scene(uv) - 50., 1., 10.);
    col 		*= f;

    col 		= pow(col, vec3(0.55)); // gamma correction

    float sdf   = scene(uv);
    fragColor   = vec4(sdf, sdf, sdf, 1.0);
}
