void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    float h = 0.0;
    for (int i = 0; i < 5; i++) {
        h += texture(iChannel0, vec2(sin(iTime / 25.0)) + uv * 0.1 * exp2(float(i))).x * exp2(float(-i));
    }
    h /= 2.0;
    vec3 col;
    if (uv.x < 0.33) col = vec3(h);
    else if (uv.x < 0.67) col = vec3(length(vec2(dFdx(h), dFdy(h))) * 32.0);
    else col = vec3(max(abs(dFdx(round(h * 10.0))), abs(dFdy(round(h * 10.0)))));
    fragColor = vec4(col,1.0);
}
