// vaiant of https://shadertoy.com/view/MfdfzM
// see there for more readable version

void mainImage( out vec4 o, vec2 u )
{
    o.xyz = iResolution;    
    float t = iTime,
          l = 31.4*length( u = (u+u-o.xy)/o.y 
                           // * mat2(cos(t-vec4(0,11,33,0))) // uncomment to cancel rotation
                         ),
          a = atan(u.y, u.x) + l,
          s = 2.*(a*36.-35.*(l+t));
    o = vec4(  smoothstep(6.,19.,l)
             * smoothstep(3./o.y,0., abs( a < ( a>l ?30.14:27.) 
                                            ? tan( a -.15*sin(s) ) / 31.4 
                                            : (u.x + .005*sin(s -( a>l ?.01*t:3.14)))*sign(a-l) + .9 )) );                                          
}
