void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = (uv - vec2(.5,.3))*2.;

    float o = (1.-sin(iTime*4.))/6.;

    float v = abs(length(st) - .5) < .01 ? 1. : 0.;
    float v2 = abs(length(st - vec2(0., o)) - .5) < .01 ? 1. : 0.;
    float v3 = abs(length(st - vec2(0., o*2.)) - .5) < .01 ? 1. : 0.;

    // Output to screen
    fragColor = vec4(v2,v3,v,1.0);
}
