float ring(float len, float radius, float width) {
    return smoothstep(radius - width, radius, len) - smoothstep(radius, radius + width * 2.0, len);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv;
    float progress = clamp(iTime, 0.0, 2.0) * 0.5;

    float progress1 = clamp(progress, 0.0, 0.8) / 0.8;

    float progress2 = clamp(progress - 0.8, 0.0, 0.8) / 0.2;

    vec2 center = 0.5 * iResolution.xy;
    // Normalized pixel coordinates (from 0 to 1)
    float len = length(fragCoord - center);


    vec3 ring1 = vec3(48.0, 256.0, 24.0);
    vec3 ring2 = vec3(24.0, 128.0, 24.0);

    float r1 = ring1.x + (ring1.y - ring1.x) * progress1;
    float r2 = ring2.x + (ring2.y - ring2.x) * progress1;

    float d1 = ring(len, r1, ring1.z);
    float d2 = ring(len, r2, ring2.z);

    float d = d1 + d2;

    st = st * 2.0 - 1.0;
    // Scale uv
    st *= 1. - d * 0.1 * (1.0 - progress2);
    // Convert back to 0 to 1 range
    st = st * 0.5 + 0.5;

    fragColor = texture(iChannel0, st) + d * 0.05 * (1.0 - progress2);
    //fragColor = vec4(d);

}

            float cubicBezier(float t, vec2 b, vec2 c) {
                vec2 a = vec2(0.0), d = vec2(1.0);
                vec2 e = mix(a, b, t);
                vec2 f = mix(b, c, t);
                vec2 g = mix(c, d, t);

                vec2 h = mix(e, f, t);
                vec2 i = mix(f, g, t);

                vec2 r = mix(h, i, t);
                return r.y;
            }
