const uint steps = 256u;

void mainImage(out vec4 O, in vec2 I)
{
    vec2 c = 1.5 * (2. * I - iResolution.xy) / iResolution.y;
    vec2 z = vec2(c);
    float weight = 1.;
    float val = 0.;
    for(uint i = 0u; i < steps; i++)
    {
        z *= mat2(z.x, -z.y, z.yx);
        z += c;
        val += weight * z.y / length(z);
        if(dot(z, z) > 1e38)
        {
            break;
        }
        weight *= 0.8;
    }
    val = abs(val) * 0.2;
    vec3 col = vec3(val * val, val, val * 1.2);
    O = vec4(col,1.0);
}
