mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float rr = 1.0 - c;
    return mat3(
        a.x * a.x * rr + c,
        a.y * a.x * rr + a.z * s,
        a.z * a.x * rr - a.y * s,
        a.x * a.y * rr - a.z * s,
        a.y * a.y * rr + c,
        a.z * a.y * rr + a.x * s,
        a.x * a.z * rr + a.y * s,
        a.y * a.z * rr - a.x * s,
        a.z * a.z * rr + c
    );
}

