float random(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return mix(0.3, 0.6, fract((p3.x + p3.y) * p3.z));
}

float inlerp(float a, float b, float value)
{
    return clamp((value - a) / (b - a), 0.0, 1.0);
}

vec3 setRoadColor(int roadStatus[8], int column)
{
    vec3 tex = vec3(0.3, 0.3, 0.45);

    switch(roadStatus[column])
    {
        case 1:
            float blink = (sin(iTime * 3.0) + 1.0) / 2.0;
            tex = mix(tex, vec3(1.0, 1.0, 0.0), blink); //yellow color
            break;
        case 2:
            blink = (sin(iTime * 5.0) + 1.0) / 2.0;
            tex = mix(tex, vec3(1.0, 0.0, 0.0), blink); //red color;
            break;
    }
    return tex;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float laneCount = 3.0;
    float yellowLineWidth = 0.025;
    float yellowLinePos = 0.4625;
    float dottedLineWidth = 0.06;

    vec3 roadColor = vec3(0.2, 0.2, 0.3);
    vec3 yellowLineColor = vec3(1.0, 1.0, 0.0);
    vec3 dottedLineColor = vec3(1.0);

    int roadStatus[8]; //1 : yellow, 2: red, default: road
    roadStatus[0] = 0;
    roadStatus[1] = 1;
    roadStatus[2] = 2;
    roadStatus[3] = 0;
    roadStatus[4] = 0;
    roadStatus[5] = 0;
    roadStatus[6] = 0;
    roadStatus[7] = 0;

    vec3 col;
    //col = texture(iChannel0, uv).xxx; //texture
    col = vec3(random(uv * 1000.0)); //random

    laneCount *= 2.0;

    float w = yellowLineWidth + ((0.5 - yellowLinePos)- yellowLineWidth);
    vec2 iuv = uv;
    iuv.x = inlerp(w, 1.0 - w, iuv.x);
    iuv.x = clamp(iuv.x, 0.0, 0.9999);

    vec2 fuv = fract(iuv * laneCount);
    vec2 fid = floor(iuv * laneCount);

    float mod1 = mod(fid.x, 2.0);

    float dottedLines = fid.x > 0.0 ? step(fuv.x, dottedLineWidth) * (1.0 - mod1) : 0.0; 
    dottedLines += fid.x < laneCount - 1.0 ? step(1.0 - dottedLineWidth, fuv.x) * mod1 : 0.0;

    float duv = fract(uv.y * 5.0);
    float dottedSegLength = 0.5;
    dottedSegLength = 1.0 - dottedSegLength;
    dottedLines *= step(dottedSegLength / 2.0, 1.0 - duv) - step(duv, dottedSegLength / 2.0);

    col *= setRoadColor(roadStatus, int(fid.x));
    col = mix(col, dottedLineColor, dottedLines);

    vec2 muv = abs(uv - 0.5);
    float yellowLines = step(yellowLinePos, muv.x) - step(yellowLinePos + yellowLineWidth, muv.x);

    col = mix(col, yellowLineColor, yellowLines);

    fragColor = vec4(col,1.0);
}
