#define FK(k) floatBitsToUint(k)^floatBitsToUint(cos(k))

float hash(vec2 p)
{
    uint x = FK(p.x);
    uint y = FK(p.y);

    return float((x*x-y)*(y*y+x)-x)/4.28e9;
}

mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);

    return mat2(c,-s,s,c);
}

float cheb(vec2 v, float k)
{
    return pow(pow(abs(v.x), k) + pow(abs(v.y), k), 1./k);
}

vec2 vor(vec2 p)
{
    float dist = 10000.;
    float id;
    vec2 i = floor(p);

    for (float x = -2.; x < 3.; ++x)
    for (float y = -2.; y < 3.; ++y)
    {
        vec2 c = vec2(x, y) + i;
        float h = hash(c);
        vec2 o = vec2(h);
        o.y = hash(o);
        mat2 r = rot(iTime * (1. + (h * 2. - 1.) * 2.412) + h * 3.);
        o *= .5;
        o *= r;


        float d = cheb(p - o - c, 1. + h * 8.);
        if (dist > d)
        {
            dist = d;
            id = h;
        }
    }

    return vec2(dist, id);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec2 v = vor(uv * 8.);
    vec3 col = vec3(v.x, 0, v.y);

    fragColor = vec4(col,1.0);
}
