// -3  thanks to shadertoyjiang ☄️
// +10 thanks to fishy ☄️

void mainImage( out vec4 o, vec2 u )
{
    vec3 R = iResolution;
    u = (u-.5*R.xy)/R.y + .02 * iTime + ceil(.4*iTime);

    for (o = 0.*o; ++R.z < 9.; o = sqrt(o*o + .07 + .02*cos(9e4*u.x)))
        o += texture(iChannel0, .1 * u + iTime / 3e4),
        u *= mat2(.38, -.33, .33, .38),
        o -= o.gbra;

    o = tanh(.1+7.*o*o*o.r);
}
