renderpass.code:
vec3 red = vec3(1.0,0.0,0.0);
vec3 green = vec3(0.0,1.0,0.0);
vec3 blue = vec3(0.0,0.0,1.0);
vec3 white = vec3(1.0,1.0,1.0);
vec3 black = vec3(0.0, 0.0, 0.0);
vec3 yellow = vec3(1.0,1.0,0.0);
vec3 orange = vec3(1.0,0.5,0.0);
vec3 violet = vec3(0.6,0.2,1.0);
vec3 cyan = vec3(0.0,1.0,1.0);
vec3 drakGreen = vec3(0.2,0.8,0.2);

float resolution = 50.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec3 colour = vec3(0.75);

// cell line
  vec2 center = fragCoord - 0.5;
  vec2 cell = fract(center / resolution);
  cell = abs(cell-0.5);
  float distToCell = 1.0 - 2.0 * max(cell.x, cell.y);

  float cellLine = smoothstep(0.0, 0.05, distToCell);

// xy axis
  float yAxis = smoothstep(0.0, 0.002, abs(center.x));
  float xAxis = smoothstep(0.0, 0.002, abs(center.y));

// smooth line
  vec2 pos = center / resolution;
  float pixelwidth = 2.5/resolution;

  // line 1
  float value = smoothstep(0.0, 1.0, pos.x);
  float functionLine1 = smoothstep(0.0, pixelwidth, abs(pos.y - value));

  // line 2
  float value2 = floor(abs(pos.x));
  float functionLine2 = smoothstep(0.0, pixelwidth, abs(pos.y - value2));

  // line 3
  float value3 = ceil(abs(pos.x));
  float functionLine3 = smoothstep(0.0, pixelwidth, abs(pos.y - value3));

  // line 4
  float value4 = fract(-abs(pos.x))+ value2;
  float functionLine4 = smoothstep(0.0, pixelwidth, abs(pos.y - value4));

  // line 5
  float value5 = pow(abs(mod(pos.x, 2.0) - 1.0),0.5);
  float functionLine5 = smoothstep(0.0, pixelwidth, abs(pos.y - value5));

  // line 6
  float value6 = abs(sin(pos.x));
  float functionLine6 = smoothstep(0.0, pixelwidth, abs(pos.y - value6));

  // mix color
  colour = mix(black, colour, cellLine);
  colour = mix(red, colour, xAxis);
  colour = mix(green, colour, yAxis);
  colour = mix(yellow, colour, functionLine1);
  colour = mix(blue, colour, functionLine2);
  colour = mix(orange, colour, functionLine3);
  colour = mix(violet, colour, functionLine4);
  colour = mix(cyan, colour, functionLine5);
  colour = mix(drakGreen, colour, functionLine6);

  fragColor = vec4(colour, 1.0);
}
