void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float a = iResolution.x / iResolution.y ;
    // Time varying pixel color
    vec3 col = texture(iChannel0,uv).rgb;
    float lm = (col.x+col.y+col.z)/3.0;
     float rlm = lm;

    vec2 st = uv*2.0f - 1.0f;


        vec3 pos = vec3(uv.x,uv.y,lm);
         // pos = pos*2.0f - 1.0f ;
          pos = normalize(pos-(1.0-lm));


     lm = step(lm,0.5f);

     float stripes = step(texture(iChannel1 ,vec2((pos.y+pos.x)/2.0 *10.0f*a)).x,.494f);
        stripes += lm;
        stripes = 1.0 - stripes;



         rlm = rlm/step(rlm , stripes) ;
       rlm =  length (rlm) ;
    // Output to screen
    fragColor = vec4(vec3(rlm),1.0);
}
