//https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
#define R iResolution.xy
#define ss(x) smoothstep(.0,.01,x)

vec2 V(vec2 c){
    return hash22(c)-.5;
}


vec4 VoronoiQ(vec2 U){

    vec2 C =floor(U+.5);       

    vec4 M = vec4(0,0,1e10,0);
    for(float x=-1.;x<=1.;x++)
    for(float y=-1.;y<=1.;y++){
        vec2 N= C+vec2(x,y),
        H=V(N)+N, D=abs(H-U);

        //quadrant orientation
        vec2 Q=sign(H-U);
        float d= max(D.x,D.y);
                //length(U-H);

        if(d < M.z 
            && Q==vec2(1,1)
            //&& Q.x>0.
           // &&d<1.5
            ){
            M.z=d;
            M.w=min(D.x,D.y);
            M.xy=H;           
        }
    }
    return M;

}

void mainImage( out vec4 O, in vec2 I )
{

    vec2 U = (I - R*.5) /R.y*5. + iTime* vec2(.5);
    vec3 col = vec3(0);

    vec2 C =floor(U+.5);       

    vec4 M = VoronoiQ(U) ;

    col= (0.5 + 0.5*cos(M.xyx+vec3(0,2,4)))*(1.-.4*M.z);
    col =mix(vec3(1),col,.6+.4*ss(M.w-.02));

    O = vec4(col,1.0);
}
