/* 
 A cool 2D heart shape combined 
 with logarithm and artan.
 14/Dec/2024
*/

#define PI 3.14159265359

// GLSL implementation of log and atan2
float myLog(vec2 uv, vec2 center) {
    vec2 offset = uv - center;
    return 6.0 * log(length(offset));
}

float myAtan2(vec2 uv, vec2 center) {
    vec2 offset = uv - center;
    return 6.0 * atan(offset.y, offset.x);
}

// The pattern function based on your formula
float funPattern(float logVal, float atanVal, float t) {
    return cos(cos(logVal - t) + 2.0 * cos(atanVal + t - abs(sin(logVal - t))));
}

// Convert hue to RGB (HSB to RGB helper function)
vec3 hsb2rgb(float h, float s, float b) {
    vec3 c = vec3(h, s, b);
    float k = mod(c.x * 6.0, 6.0);
    float f = k - floor(k);
    float p = c.z * (1.0 - c.y);
    float q = c.z * (1.0 - f * c.y);
    float t = c.z * (1.0 - (1.0 - f) * c.y);

    if (k < 1.0) return vec3(c.z, t, p);
    if (k < 2.0) return vec3(q, c.z, p);
    if (k < 3.0) return vec3(p, c.z, t);
    if (k < 4.0) return vec3(p, q, c.z);
    if (k < 5.0) return vec3(t, p, c.z);
    return vec3(c.z, p, q);
}

// https://github.com/d3/d3-color
vec3 cubehelix(vec3 c) {
  float a = c.y * c.z * (1.0 - c.z);
  float cosh = cos(c.x + PI / 2.0);
  float sinh = sin(c.x + PI / 2.0);
  return vec3(
    (c.z + a * (1.78277 * sinh - 0.14861 * cosh)),
    (c.z - a * (0.29227 * cosh + 0.90649 * sinh)),
    (c.z + a * (1.97294 * cosh))
  );
}

// https://github.com/d3/d3-scale-chromatic
vec3 cubehelixDefault(float t) {
  return cubehelix(vec3(mix(300.0 / 180.0 * PI, -240.0 / 180.0 * PI, t), 0.5, t));
}

// https://github.com/d3/d3-scale-chromatic
vec3 cubehelixRainbow(float t) {
  if (t < 0.0 || t > 1.0) t -= floor(t);
  float ts = abs(t - 0.5);
  return cubehelix(vec3((360.0 * t - 100.0) / 180.0 * PI, 1.5 - 1.5 * ts, 0.8 - 0.9 * ts));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = ( fragCoord -.5 * iResolution.xy ) / iResolution.y;

    vec2 center = vec2(0.0, 0.0); // Center coordinates (u, v) to another point, if you want :)

    // Compute pattern values
    float logVal = myLog(uv, center);
    float atanVal = myAtan2(uv, center);
    float patternValue = funPattern(logVal, atanVal, iTime);


    // Assign color based on patternValue
    vec3 color;
    if (patternValue < 0.0) {
        float hue = mix(0.0, 1.0, (patternValue + 1.0) / 1.0); // Map [-1, 0] to [0, 1]
        //color = hsb2rgb(hue, 1.0, 1.0); // Usual HSV color
				color = cubehelixRainbow(hue); // I like more these Rainbow colors compare to the usual HSV
    } else {
        color = vec3(0.15); // Dark gray for positive pattern value
    }

    // Output to screen
    fragColor = vec4(color,1.0);
}
