#define _0 vec2(0., 12.)
#define _1 vec2(1., 12.)
#define _2 vec2(2., 12.)
#define _3 vec2(3., 12.)
#define _4 vec2(4., 12.)
#define _5 vec2(5., 12.)
#define _6 vec2(6., 12.)
#define _7 vec2(7., 12.)
#define _8 vec2(8., 12.)
#define _9 vec2(9., 12.)
#define _DOT    vec2(14., 13.)
#define _DASH   vec2(13., 13.)

#define ADVANCE_ST(_v) (st.x += _v*0.5*_dsize.x)
#define TRANSLATE_ST(_v) (st.x + _v * 0.5*_dsize.x)
#define MAX_PRE_DOT_DIGITS 4.
#define MAX_POST_DOT_DIGITS 1.

#define INC_COL(a) (a += 1.1*d_size.y)

float printSymbol(in vec2 _uv, in vec4 _dbox, in vec2 _symb)
{
    vec2 inside = step(_dbox.xy, _uv) - step(_dbox.xy + _dbox.zw, _uv);
    if (inside.x * inside.y < 0.5)
        return 0.;

    vec2 uv = clamp((_uv - _dbox.xy) / _dbox.zw, 0.1, 0.9);
    float f = texture(iChannel2, 0.0625 * (_symb + uv)).w;
    return 75.*smoothstep(.5, 0.1, f);
}

float printFixedNumber(in vec2 _uv, in vec2 _dstart, in vec2 _dsize, in float _num)
{
    vec2 st = _dstart;

    float hnd_digit = floor(_num * 0.01);
    float ten_digit = floor(mod(_num * 0.1, 10.));
    float tenth_digit = floor(fract(_num) * 10.);

    float f;
    f += printSymbol(_uv, vec4(st, _dsize), vec2(hnd_digit, 12.));
    ADVANCE_ST(1.1);
    f += printSymbol(_uv, vec4(st, _dsize), vec2(ten_digit, 12.));
    st.y -= _dsize.y*0.3;
    st.x -= _dsize.x*0.01;
    //f += printSymbol(_uv, vec4(st, _dsize), _DOT);
    //ADVANCE_ST(0.75);
    //f += printSymbol(_uv, vec4(st, 0.5*_dsize), vec2(tenth_digit, 12.));

    return f;
}
#ifdef DEBUG_PRINT

float printNumber(in vec2 _uv, in vec2 _dstart, in vec2 _dsize, in float _num)
{
    vec2 symb;
    vec2 st = _dstart;
    float f = 0.;
    if (_num < 0.) {
        f = printSymbol(_uv, vec4(st, _dsize), _DASH);
        _num *= -1.;
        ADVANCE_ST(1.);
    }
    float num = _num;
    float div = 1.;
    float nd = 0.;
    for (;nd < MAX_PRE_DOT_DIGITS && num >= div; nd++, div *= 10.);
    //f += printSymbol(_uv, vec4(st, _dsize), vec2(nd, 12.));

    ADVANCE_ST(nd);

    // before the dot
    float inum = 0.;
    for (float id = 0.; id < nd; id++) {
        float digit = floor(mod(num, 10.));
        inum += digit * (id + 1.);
        num /= 10.;
        f += printSymbol(_uv, vec4(TRANSLATE_ST(-(id + 1.)), st.y, _dsize), vec2(digit, 12.));
    }
    if (nd == 0.0)
    {
        ADVANCE_ST(1.);
        f += printSymbol(_uv, vec4(TRANSLATE_ST(-1.), st.y, _dsize), _0);
    }

    f += printSymbol(_uv, vec4(st, _dsize), _DOT);

    num = _num - inum;
    // print 2 digits after the dot
    ADVANCE_ST(1.); //
    for (float id = 0.; id < MAX_POST_DOT_DIGITS; id++) {
        num *= 10.;
        float digit = floor(mod(num, 10.));
        f += printSymbol(_uv, vec4(st, _dsize), vec2(digit, 12.));
        ADVANCE_ST(1.);
    }


    return f;
}
#endif


#define TFC(CRD) texelFetch(iChannel0, ivec2(CRD),0)
#define TEX(UV)  texture(iChannel0, UV)
#define MAX_KRN_SIZE 5.

const vec2 vhex = normalize(vec2(1., 0.5));
const float hexh = 0.8660254037; // = sqrt(3) / 2;
const float inv_hexh = 1.15470053837;
const vec2 hexGrid = vec2(3., sqrt(3.));

vec4 draw_hud(in vec2 _uv, vec4 _params) {
    vec3 o = TF1(ORIG_POS).xyz;
    vec3 d = TF1(ORIG_DIR).xyz;
    vec3 u = TF1(ORIG_UP).xyz;
    vec4 prox = TF1(PROX_SNS);
    vec4 hitp = TF1(D_HIT_POS);
    float ta25 = PULSE_T(mod(iTime, 60.), 2., 25., 47.);
    float anim_sns_ta25 = 1. + ta25;
    vec3 r = cross(d, u);


    float ratio = iResolution.x/iResolution.y;
    //ratio = iResolution.y/iResolution.x / HUD_TARGET_RATIO;
    vec2 cuv = _uv;
    CURVE_UV(cuv);
    float luv = length(cuv);

    // patterns
    float y_pattern = pow(abs(0.25 + 2.*cos(cuv.y*200.)), 2.);
    float x_pattern = pow(abs(0.25 + 2.*cos(cuv.x*200.)), 2.);
    float xy_pattern = 0.15*(y_pattern + x_pattern);
    ivec2 ch_grid = ivec2(cuv * 200.);
    float ch_pattern = max(0.1 + float(abs(ch_grid.x + ch_grid.y) % 2), 1.);
    vec4 prox_active = smoothstep(2., 1., prox);
    float hit = smoothstep(7.5, 0., hitp.w);
    float vel_start = mix(1., 0., clamp((_params.x * _params.x * 0.25), 0., 1.));


    vec2 ang_cuv = ANG_UV(cuv);
    vec2 sqr_cuv = STR_UV(cuv, 1.5);

    float f = hud_frame(_uv, ratio, vec3(HUD_DIM * vec2(ratio, 1.), 0.695));
    vec2 fc_uv = vec2(f, abs(abs(_uv.y)-0.4*HUD_DIM) - 0.02);
    fc_uv.y = abs(fc_uv.y);
    float fsc = max(sdTrapezoid(fc_uv, 0.2, 0.00, 0.15), -(f+0.05));

    f = max(f, -fsc);
    float hf = smoothstep(0., 0.01, -f);

    // white on borders
    float to_white = 0.5*luv*luv*dot(normalize(vec2(0.15*_uv.x, _uv.y)), vec2(1., 0.));
    vec3 col = hf*mix(HUD_BASE_CLR,vec3(1.), to_white * to_white);

    col += vel_start * vec3(1., 0.4, 0.1)*PULSE_T(-f, 0.01, 0.05, 0.051);

    // white frame
    float wf = hud_frame(_uv, ratio, vec3(HUD_DIM * vec2(ratio*1.03, 0.95), 0.705));
    wf = max(wf, -(f+0.011));
    wf = smoothstep(0., 0.01, -wf);
    col +=  ch_pattern * wf * vec3(1., 0., 0.) * (0.995*cuv.y*cuv.y);

    vec2 ang_grid = ang_cuv * vec2(1., 1.);
    vec2 iang_grid = floor(ang_grid);
    vec2 fang_grid = fract(ang_grid);
    float border = 0.1;
    float cnt_d_size = mix(0.22, 0.22, smoothstep(0., 1., _params.x*0.1));
    float fline = sdSegment(vec2(ang_grid.x - cnt_d_size, fract(ang_grid.y + 0.5)), vec2(0., border),
                                    vec2(0., 1.-border));

    float snsGrid = mod((ang_grid.y*4. + 0.5), 4.);
    float fcring = PULSE_T(ang_grid.x, 0.02, 0.15, 0.16);// sdSegment(vec2(ang_grid.x - cnt_d_size, fract(ang_grid.y + 0.5)), vec2(0., 3.), vec2(0.,7.));
    float fprox_sns = PULSE_T(ang_grid.x, 0.01, 0.155, 0.155);
    fprox_sns *= PULSE_T(fract(snsGrid), 0.05, 0.25, 0.75);
    fcring -= fprox_sns;
    float fring = PULSE_T(ang_grid.x, 0.005, 0.27, 0.27);
    fring += PULSE_T(ang_grid.x, (0.005 + 0.005*ta25), 0.28, 0.28);

    float froll = anim_sns_ta25 * 0.75 * PULSE_T(fract(ang_grid.y * 2.), 0.1, 0.4 + _params.y*0.75, 0.6 + _params.y*0.75) * PULSE_T(ang_grid.x, 0.01, 0.31, 0.31);

    float nticks = mix(30., 150., clamp((iResolution.y - 300.)/1000., 0.,1.));
    float fcticks = PULSE_T(fract(ang_grid.y * nticks), 0.2, 0.5, 0.5);
    float ftick_hlt = mix(1., 20., step(1.-fract(ang_grid.y + 0.5), _params.x*0.1));
    float fdbl = PULSE_T(fline, 0.005, 0.0195, 0.02);

    // sensors
    float fside_sns = PULSE_T(sqr_cuv.x, 0.01, 0.375, 0.4);
    fside_sns *= PULSE_T(fract(snsGrid), 0.05, 0.25, 0.75);

    // single line
    float fsng = smoothstep(0.015, 0., fline);
    //fline = mix(fdbl, fsng, clamp(_params.y, 0., 1.));
    col += fdbl * mix(HUD_BASE_CLR.bgr, vec3(1.), 0.25);
    col += fsng * fcticks * mix(fract(ang_grid.y - 0.5)*HUD_BASE_CLR.bgr*ftick_hlt, vec3(1.), 0.25);
    col += (vec3(1.)*froll);
    col += fring*vec3(1., 0.75, 0.5)*1.5*ta25;
    //col += 1.25*fcring;
    col += fprox_sns * mix(HUD_BASE_CLR.rgb,
               vec3(1.75, 0.7, 0.1), 
               hit * 
               ORIG2UNIT(cos(iTime*PI2)));

    col += fside_sns * ch_pattern * prox_active[int(snsGrid)];

    vec2 rad_uv = cuv - vec2(HUD_DIM.x * ratio * 0.7, -0.4);
    float fradar = exp(-sdfOcta(rad_uv)*3.3);
    fradar = smoothstep(0.4, 1., fradar);
    float rscale = 80. + _params.x*5.;
    vec3 rad_p = o + rscale * (r * rad_uv.x + d * rad_uv.y);
    vec3 gyro = double_gyroid(rad_p, get_gyroid_t(rad_p, iTime),
                                iTime, 0.);
    // borders
    const vec3 border_col = vec3(1., 0.75, 0.5);
    vec3 gyro_col = mix(border_col, border_col*3., ta25)*PULSE_T(gyro.x, 0.1, -0.01, 0.01);
    gyro_col += (1. + 49.*ta25)*smoothstep(0.1, 0., fradar);
    float i_f = mix(0.5, 1.5, floor(abs(gyro.x * 4.))/4.);
    // brown tunnel
    gyro_col += ch_pattern * mix(INV_TUNNEL0_CLR.bgr * i_f, 
        vec3(0.5), 0.75) * smoothstep(0., 1., gyro.y) * smoothstep(0., 1., -gyro.z);
    // blue tunnel
    gyro_col += ch_pattern * mix(INV_TUNNEL1_CLR.bgr * i_f, vec3(0.5), 0.75) * smoothstep(0., 1., gyro.y) * smoothstep(0., 1., gyro.z);
    gyro_col += (mix(HUD_BASE_CLR.rbg, vec3(0.1, 0.79, 0.96), 0.5)*xy_pattern) * smoothstep(0., 1., -gyro.y);
    gyro_col *= smoothstep(0., 0.2, fradar);
    gyro_col += 3.*PULSE_T(fradar, 0.01, 0.05, 0.05);

    // self character
    float fself = sdEquilateralTriangle(vec2(rad_uv.x, rad_uv.y + 20.*(rad_uv.x*rad_uv.x)), 0.02);
    gyro_col += mix(vec3(1.), vec3(10., 1.23, 0.2), ta25) *0.75*smoothstep(0.005, 0., abs(fself) - 0.0001);

    vec2 d_size = vec2(0.35 / ratio, 0.2);
    float fvel = 1.5*printFixedNumber(cuv, vec2(-d_size.x*0.76, -d_size*0.5), d_size, _params.x*30.);
    col += fvel * mix(3.*HUD_BASE_CLR.gbb, vec3(1.), 0.);
    col += fradar * gyro_col;

    float alpha = max(0.5*hf, 0.75*wf);
    alpha = mix(alpha, 0.75, fcring);
    alpha = mix(alpha, 0.75, fring);
    alpha = mix(alpha, 0.75, fsng * fcticks);
    alpha = mix(alpha, 0.75, froll);
    alpha = mix(alpha, 0.5, fvel);
    alpha = max(alpha, 0.9*smoothstep(0., 0.1,fradar));
    return vec4(col, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ruv = fragCoord / iResolution.xy;
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 muv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;
    vec4 col = texture(iChannel0, ruv);
    float roll = TF1(ORIG_DIR).w;
    float vel = TF1(ORIG_VEL).x;
    float fps = TF1(FPS_DATA).y;
    float ratio = iResolution.x/iResolution.y;

    uv = ROT2D(uv, roll);
    //float frame = hud_frame(uv, ratio,
      //                  vec3(HUD_DIM * vec2(ratio*10.5, 1.1), 100.815));

    float rdepth = col.w/FAR;
    // post processing  
    col.rgb = mix(col.rgb, 0.25 * col.rgb, rdepth * rdepth);
    col.rgb = mix(col.rgb, vec3(0., 0.36, 0.7), 1.-exp(-rdepth*5.));
    col *= 2.;
    col /= (1. + col);
    col.rgb = pow(col.rgb, vec3(2.2));


    vec4 hcl = draw_hud(uv, vec4(vel, roll, 0., 0.));
    // draw hud
    col.rgb = mix(col.rgb * smoothstep(0.,0.5, hcl.w), hcl.rgb, hcl.w);

    #ifdef DEBUG_PRINT
    vec2 d_size = vec2(0.05 / ratio, 0.05);
    vec4 hit = TF1(D_HIT_POS);

    vec4 dvals = TF1(D_VALS);
    vec3 rd = TF1(ORIG_DIR).xyz;
    float col_up;
    //col.rgb += printNumber(ruv, vec2(0.01, 0.01), d_size, fps);
    //col.r += printNumber(ruv, vec2(0.01, 0.01 + INC_COL(col_up)), d_size, dvals.y);
    //col.g += printNumber(ruv, vec2(0.01, 0.01 + INC_COL(col_up)), d_size, dvals.z);
    //col.g += printNumber(ruv, vec2(0.01, 0.01 + INC_COL(col_up)), d_size, dvals.w);

    #endif

    fragColor = vec4(col);
}

#define ATF(_crd, _pos, _val) if (_crd.x == _pos.x && _crd.y == _pos.y) { col = _val; }
#define N_LATERAL_SENSORS 6
#define N_SENSORS 2 * N_LATERAL_SENSORS + 1

vec4 collision_dirs[N_SENSORS]; // collision directions

// this is just a short notation
#define CLS collision_dirs

// calculate direction score based on distance and current direction
float d_score(in vec3 _v, in float _dist, in vec3 _pv, in float _vel_factor) {

    return mix(dot(_pv, _v), _dist, 0.5);
}

float eps_sign(in float _v, in float _eps) {
    float rv = step(_eps, _v) - step(_v, -_eps);
    return rv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col;
    ivec2 crd = ivec2(fragCoord);


#if N_LATERAL_SENSORS == 4
    const vec4 dir_cf = vec4(ARCTAN_30, -ARCTAN_30, ARCTAN_60, -ARCTAN_60);

#else  
    const float[N_LATERAL_SENSORS] dir_cf = float[6](ARCTAN_30, -ARCTAN_30,
                                     1., -1.,
                                     ARCTAN_60, -ARCTAN_60);
#endif    
    // update controls
    if (crd.x < CONTROL_ZONE.x && crd.y < CONTROL_ZONE.y) {
        vec3 o = TF0(ORIG_POS).xyz;
        vec3 d = normalize(TF0(ORIG_DIR).xyz);
        float roll = TF0(ORIG_DIR).w;
        vec3 u = normalize(TF0(ORIG_UP).xyz);
        vec4 v = TF0(ORIG_VEL);
        vec3 r = normalize(cross(d, u));
        vec4 pd = TF0(PREV_DIR); // previous direction
        vec3 ppd = TF0(PP_DIR).xyz;
        vec4 fps_data = TF0(FPS_DATA);

        // initialize
        if (iFrame == 0){
            ATF(crd, ORIG_POS, vec4(start_pos, 0.));
            ATF(crd, ORIG_DIR, vec4(start_dir, 0.));
            ATF(crd, ORIG_UP, vec4(start_up, 0.));
            ATF(crd, ORIG_VEL, vec4(0., start_dir));
            ATF(crd, D_HIT_POS, vec4(0.));
            ATF(crd, PREV_DIR, vec4(start_dir, 0.));
            ATF(crd, D_VALS, vec4(0.));
            ATF(crd, PP_DIR, vec4(start_dir, 0.));
        }
        else {
            vec3 vd = d;
            vec4 dvals;
            // setup directions for collision detection
            collision_dirs[0] = vec4(d, 0.);
            for (int id = 1; id < N_LATERAL_SENSORS + 1; id++) {
                collision_dirs[id] = vec4(normalize(d + u * dir_cf[id - 1]), 0.);
                collision_dirs[id + N_LATERAL_SENSORS] = vec4(normalize(d + r * dir_cf[id - 1]), 0.);
            }

            // compute distance to collision in the current course
            for (int id = 0; id < N_SENSORS; id++)
                collision_dirs[id].w = traceGeomHit(o, collision_dirs[id].xyz, vec4(iTime, 0., 0., 0.));

            vec3 d_hit_pos = o + d * collision_dirs[0].w;

            // choose new direction based on distance
            // The primary direction is given less weight
            // to explore more.
            float prim_dir_weight = mix(1., primary_dir_weight, v.x/max_velocity);

            // we want to be pretty decisive in low velocities and 
            // being able to fluctuate in higher
            float vel_factor = mix(20., 5., v.x/max_velocity);
            float I = mix(0., 1., step(d_score(CLS[0].xyz, prim_dir_weight*CLS[0].w, pd.xyz, vel_factor),
                                       d_score(CLS[1].xyz, CLS[1].w, pd.xyz, vel_factor)));

            // There is score computation duplication here but
            // it's rather cheap and maybe better than storing 
            for (int id = 2; id < N_SENSORS; id++)
                I = mix(I, float(id), step(
                                    d_score(CLS[int(I)].xyz,CLS[int(I)].w, pd.xyz, vel_factor), 
                                    d_score(CLS[id].xyz, CLS[id].w, pd.xyz, vel_factor)));

            // chosen direction
            vec3 cv = CLS[int(I)].xyz;

            // can't play with the stick too much :(
            // otherwise it will be shaky
            float f_update_time = (iTime - pd.w) / dir_update_wait;

            float dir_update = step(1., f_update_time);

            ppd = mix(ppd, pd.xyz, dir_update);
            cv = mix(pd.xyz, cv, dir_update);
            float uf = fract(f_update_time*2.);
            uf = uf*uf*uf*(uf*(uf*6.0-15.0)+10.0);
            vec3 ccv = normalize(mix(ppd, cv, uf));
            float update_time = mix(pd.w, iTime, dir_update);
            // update direction

            // Get Theta and Phi
            mat3 rot = mat3(r, u, d);
            vec3 trf_cv = transpose(rot) * cv;
            float rcv = length(trf_cv);
            float phi = asin(trf_cv.y/rcv);
            float theta = atan(trf_cv.x, trf_cv.z);

            //theta += 0.05 * noise(iTime * 0.01) * exp(-v.x*10.) * iTimeDelta;
            // rotate lateral angle
            vec3 dd = rotate(d, u, -eps_sign(theta, ANG_EPS) * iTimeDelta * rad_per_sec * 0.0);
            d = rotate(d, u, -eps_sign(theta, ANG_EPS) * iTimeDelta * rad_per_sec);
            // update r
            r = normalize(cross(d, u));
            // rotate axial angle
            d = rotate(d, r, eps_sign(phi, ANG_EPS) * iTimeDelta * rad_per_sec);
            // update u
            u = normalize(cross(r, d));

            // update roll

            float droll = 0.1 * iTimeDelta * mix(step(ANG_EPS, abs(roll))*-sign(roll), 
                -eps_sign(theta, ANG_EPS), 
                step(ANG_EPS, abs(theta)));

            float inc_roll = step(abs(roll + droll), PI/6.);
            roll += droll * inc_roll;
            u = rotate(u, d, droll * inc_roll);
            r = normalize(cross(d, u));

            // update acceleration
            // add deceleration on turns based on velocity.
            float accf = clamp(mix(0.25 * acc_factor, acc_factor, v.x / (10.)),0., acc_factor);
            float acc = accf - 1. * v.x*(abs(theta) + abs(phi)); //

            // stopping distance is relative to current velocity
            float stop_dist = max(v.x, min_stopping_distance);

            // This is pushing on breaks to avoid collision.
            float dcc = -1.5 * (v.x + collision_dirs[0].w);
            acc = mix(dcc, acc, smoothstep(stop_dist - min_stopping_distance, 
                                           stop_dist + min_stopping_distance, collision_dirs[0].w));


            vd *= v.x;
            // drag on current velocity;
            vd -= d * drag * v.x * v.x * iTimeDelta;
            vd += acc * d * iTimeDelta;
            // update velocity

            // that's just for practical visualization reasons
            v.x = length(vd);
            v.yzw = normalize(vd);
            vd = v.x * v.yzw;
            // update position
            o += vd * iTimeDelta;

            r = cross(d, u);
            u = cross(r, d);

            //u = rotate(u, d, 0.5*roll_th*iTimeDelta);
            //r = cross(d, u);

            // proximity sensors
            // the order is ccw starting from the bottom
            vec4 prox;
            prox.x = traceGeomHit(o, d + normalize(d - u * ARCTAN_80), vec4(iTime, 0., 0., 0.));
            prox.y = traceGeomHit(o, d + normalize(d - r * ARCTAN_80), vec4(iTime, 0., 0., 0.));
            prox.z = traceGeomHit(o, d + normalize(d + u * ARCTAN_80), vec4(iTime, 0., 0., 0.));
            prox.w = traceGeomHit(o, d + normalize(d + r * ARCTAN_80), vec4(iTime, 0., 0., 0.));


            // DEBUG VALUES
            //dvals.x = ppd.x;
            //dvals.y = ppd.y;
            //dvals.z = cv.x;
            //dvals.w = cv.y;
            //
            ATF(crd, D_VALS, dvals);
            ATF(crd, ORIG_POS, vec4(o, 0.));
            ATF(crd, ORIG_DIR, vec4(d, roll));
            ATF(crd, ORIG_UP, vec4(u, 0.));
            ATF(crd, ORIG_VEL, vec4(v));
            ATF(crd, PREV_DIR, vec4(cv, update_time)); // record the timestamp of previous change
            ATF(crd, D_HIT_POS, vec4(d_hit_pos, collision_dirs[0].w));
            ATF(crd, PP_DIR, vec4(ppd, 0.));
            ATF(crd, PROX_SNS, prox);

            ATF(crd, FPS_DATA, fps_data);
            if (iFrame % 20 == 1) {
              float fps = 20. / (iTime - fps_data.x);
              ATF(crd, FPS_DATA, vec4(iTime, fps, vec3(0.)));
            }
        }
    }

    fragColor = vec4(col);
}

