vec4 geomI(in vec3 p, in bool high)
{
    float tval = get_gyroid_t(p, iTime);
    vec3 gf = double_gyroid(p, tval, iTime, float(high));

    float rf = (high) ? 0.1*fbm(p) : 0.0;
    gf.x += rf;

    // create holes
    float s_len = get_slen(p, iTime, 0.5) ;
    vec3 r = (p * PI2) / s_len;

    float ad = max(length(p)*0.025, 0.);
    float holeGrid = 0.05;
    vec3 q = r - holeGrid*floor((r+0.5*holeGrid)/holeGrid);
    float hf = 0.4*holeGrid;
    float f = length(q)- hf;

    gf.x = max(gf.x, -f);

    //gf.x *= -1.;
    return vec4(gf, rf);
}

vec3 cp;

vec4 traceI(in vec3 o, in vec3 d, in vec2 crd) {
  float salt = UNIT2ORIG(hash(crd.x*iResolution.y + crd.y));
  float vel = TF0(ORIG_VEL).x;
  float t = 0.;
  float mint = 10.0;
  vec3 vd = TF0(ORIG_DIR).xyz;
  vec3 u = TF0(ORIG_UP).xyz;
  vec3 r = cross(vd, u);
  vec3 std = d;
  const float campl = 5.;
  const float fq = 1.;
  vec2 res = vec2(mint, 0.);
  cp = o;
  for (int i=0 ; i < M_ITER ; i++)
  {
    //vec3 p = o + t*std;

    res = geomI(cp, false).xz;
    mint = abs(res.x);
    t += mint;

    //r = cross(std, u);
    float ampl = mix(1., campl, (t*t)/(FAR*FAR));// * pow(0.5+t/FAR, 1.);
    std = normalize(d + r * (vel*salt*0.02 + 1.) * UNIT2ORIG(ampl*noise(t*fq)
                                + 0.35*ampl*noise(t*fq*2.01 + 23.11)
                                + 0.125*ampl*noise(t*fq*4.01 + 11.34 + 4.*salt)));

    if (mint < T_EPS*t || t > FAR) break;

    cp += std*mint;
  }


  return vec4(t, mint, res.y, res.y);
}

vec3 normI(in vec3 p, in bool hres)
{
    vec3 n;
    vec2 e = vec2(N_EPS*10., 0.0);

    n.x = geomI(p + e.xyy, hres).x - geomI(p - e.xyy, hres).x;
    n.y = geomI(p + e.yxy, hres).x - geomI(p - e.yxy, hres).x;
    n.z = geomI(p + e.yyx, hres).x - geomI(p - e.yyx, hres).x;

    return normalize(n);
}

vec3 normfI(in vec3 p, in bool hres)
{
    vec3 n;
    vec2 e = vec2(N_EPS*10., 0.0);

    float f = geomI(p, hres).x;
    n.x = geomI(p + e.xyy, hres).x - f;
    n.y = geomI(p + e.yxy, hres).x - f;
    n.z = geomI(p + e.yyx, hres).x - f;

    return normalize(n);
}


// Fresnel-Shlick
float F(in float _f0, in vec3 _h, in vec3 _v)
{
    float hv = max(dot(_h, _v), 0.);
    float hv1 = pow(1. - hv, 5.);
    return _f0 + (1. - _f0) * hv1;
}

vec4 triplanar(in vec3 p, in vec3 n, in vec3 scale, in sampler2D s)
{
  float sw = n.x + n.y + n.z;
  float wx = n.x/sw;
  float wy = n.y/sw;
  float wz = n.z/sw;

  vec4 dx = texture(s, p.yz*scale.yz);
  vec4 dy = texture(s, p.zx*scale.zx);
  vec4 dz = texture(s, p.xy*scale.xy);

  return dx * wx + dy *wy + dz * wz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x/iResolution.y;

    vec2 uv = (2.0*fragCoord-iResolution.xy)/(iResolution.y);

    float fov = 1.5 + 2.5 * PULSE_T(mod(iTime, 30.), 2., 10., 28.);

    float t25 = PULSE_T(mod(iTime, 60.), 2., 25., 47.);
    vec3 o = TF0(ORIG_POS).xyz;
    vec3 d = TF0(ORIG_DIR).xyz;
    vec3 u = TF0(ORIG_UP).xyz;
    float roll = TF0(ORIG_DIR).w; // 

    uv = ROT2D(uv, roll);
    vec2 duv = uv;

    // make this line as macro/function
    CURVE_UV(duv);

    vec3 od = normalize(vec3(fov*(duv),-1.));
    vec3 light = normalize(vec3(1., -1., -1.));

    mat3 view;

    vec3 col;
    vec3 bckcol = vec3(0.);
    vec4 ccol = vec4(0.);

    float depth = 0.0;

#ifdef HUD_ONLY
    float frame = hud_frame(uv, ratio,
                        vec3(HUD_DIM * vec2(ratio*10.5, 1.1), 100.815));
#else
    // in this case I care only about the top and bottom cut
    float frame = hud_frame(uv, ratio,
                        vec3(HUD_DIM * vec2(ratio*10.5, 1.1), 100.815));
    float sm_col = smoothstep(0.0,-0.01, frame);
#endif    
    if (frame > 0.00)
    {
        fragColor = vec4(0.);
        return;
    }

    vec3 r = cross(d, -u);
    d = mat3(cross(d, u), u, -d) * od;
    //d = camera(o, d, target, view);

      vec4 g3res = traceI(o, d, fragCoord);
      // hit position to gyroid.
      vec3 totp = cp;//o + g3res.x * d;
      vec3 n3gyr = normfI(totp, true);
      vec3 n = n3gyr;

      vec4 mtl = geomI(totp, true);

      float inside = step(0.1, mtl.y);

      vec3 icol = triplanar(totp, n*n, vec3(0.1), iChannel2).rgb;
      vec3 gcol = mix(INV_TUNNEL0_CLR, INV_TUNNEL1_CLR, step(0.1, mtl.z));
      vec3 inv_gcol = mix(INV_TUNNEL1_CLR, INV_TUNNEL0_CLR, step(0.1, mtl.z));
      gcol *= mix(icol.rrr, icol, t25);
      // differentiate inside/outside
      gcol *= (1. - 0.5*inside);

      depth += g3res.x;

      // warp the normal a bit
      vec3 rn = normalize(cross(n3gyr, u));
      vec3 un = normalize(cross(rn, n3gyr));

      n3gyr = normalize(mix(n3gyr, 
                  n3gyr + 2.*mix(rn * UNIT2ORIG(hash(totp.x)), 
                      un * UNIT2ORIG(hash(totp.y)), noise(totp.z)),
                      0.1*noise(g3res.x)));


      gcol *= (1. + 0.5*noise(length(0.25*totp)));
      gcol = mix(vec3(1.), gcol, 0.9 + 0.1*noise(length(totp) + 23.11));

      // bright one of the two gyroids.
      gcol *= (1. + 2.*step(0.1, mtl.z));
      gcol += t25 * (gcol*1.*step(mtl.z, 0.1));

      // light direction towards the cube.
      vec3 lpos0 = (o - 0.5*r);
      vec3 ldir = normalize(totp - lpos0);

      vec3 rfl = reflect(d, n3gyr);
      vec3 h = normalize(ldir - d);
      vec3 rflcol = 2.*mix(vec3(1.), inv_gcol, length(cross(n, d)));
      vec3 spec = rflcol*(max(dot(rfl, -ldir), 0.) + F(0.1, h, d));
      vec3 acol = (0.1 + gcol*max(0.9*dot(n3gyr, -ldir), 0.0)) + spec;

      gcol = gcol*(acol);

      bckcol += gcol;

    col = mix(bckcol, ccol.rgb, pow(clamp(1.02*ccol.a, 0., 1.), 2.));
    col = col.bgr;
#ifndef HUD_ONLY
    col = max(vec3(0.), col)*sm_col;
#endif
    //col = mix(col, col.bgr, _ANIMATION(11));

    //col = mix(col, tcol.rgb, (step(0.1, ccol.a) - step(0.75, ccol.a)) * 0.5 * smoothstep(1.2, 1.8, length(col - tcol.rgb)) );
    // Output to screen
    fragColor = vec4(col, depth);
}

