vec3 rotate(in vec3 _v, in vec3 _k, in float _a) {
    float ca = cos(_a);
    float sa = sin(_a);

    vec3 rv = _v * ca + cross(_k, _v) * sa + _k * dot(_k, _v) * (1. - ca);

    return normalize(rv);
}

