vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h, -b+h );
}

float hud_frame(in vec2 _uv, float _r, in vec3 _hdims) {
    vec2 cuv = _uv;
    // add curvature
    CURVE_UV(cuv);

    float fb = sdBox(cuv, _hdims.xy);
    float fl = dot(abs(_uv), normalize(vec2(1.))) - _hdims.z*sqrt(_r*_r + 1.);
    float f = max(fl, fb);

    return f;
}

float get_gyroid_t(in vec3 p, in float _time) {
    return 0.7 + 0.1*noise(p.z);
}

float get_slen(in vec3 p, in float _t, in float _factor) {
   //return PI2 * (3. + 0.8*sin(_t * 0.45 + p.x*0.5) + _factor * (18. + noise3(p*0.1) + 0.15*sin(5.*_t)-2.5*(log(length(p)))) );
    return PI2 * (5. + 0.01*pow(0.5 + 0.5 * sin(_t * 2. + 0.02*p.x), 2.));
}
vec3 double_gyroid(in vec3 p, in float t, in float _time, in float high)
{
    vec3 r;
    float s_len = get_slen(p, _time, 0.5);

    float fn = dot(cos(p*PI2 / s_len), sin(p.yzx * PI2 / s_len));

    r.x = fn*fn - t*t;

    // differentiate gyroid
    r.z = sign( (abs(fn - t) - abs(fn + t)) );

    // differentiate side of the gyroid
    r.y = sign( r.x );

    r.x = 0.5*abs(r.x);

    return r;
}


// returning positive for negative values
float geom_c(in vec3 _p, in float _time) {
    float tval = get_gyroid_t(_p, _time);    
    return 0.25*double_gyroid(_p, tval, _time, 1.).x;
}

float traceGeomHit(in vec3 _o, in vec3 _d, in vec4 _param) {
    float t = 0., ft = 10.;
    float i = 0.;
    vec3 p;
    for (i = 0.; i < MAX_ITER; i++) {
        p = _o + t * _d;
        ft = geom_c(p, _param.x);

        if (ft > FAR || abs(ft) < T_COL_EPS * t) {
            break;
        }

        t += ft;
    }

    return t;
}

