mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 3.5;
    vec3 col = vec3(0);
    float r = cos(iTime)* sin(length(uv));
    uv *= Rot(r*9.);
    vec2 gv = fract(uv)-.5; // grid


    vec2 id = floor(uv);
    gv += id*.7; // -=, /= also give interesting results


    //float s = .8/length(gv-.5);
    //col += s;
    col.rg -= gv;

    fragColor = vec4(col,1.0);
}
