void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float vltime = (sin(iTime * 2.0) + 1.4)*.35;
    vec4 cl = vec4(floor((uv.y + .25) * 2.0), floor((uv.y + .25) * 10.0), 1.0 - floor((uv.y + .25) * 2.0), 100.0);
    vec2 uvm = vec2(((uv.x - .5) * 1.8), (uv.y - vltime));
    float ff = sqrt((uvm.y * uvm.y) + (uvm.x * uvm.x));
    vec4 cr =  vec4 (0,floor(ff * 8.0), 0, 1000);

    float vbtime = (sin((iTime -.5) * 2.0) + 1.4)*.35;
    vec2 uvi = vec2(((uv.x - vbtime) * 1.8), (uv.y - .5));
    float fu = sqrt((uvi.y * uvi.y) + (uvi.x * uvi.x));
    vec4 zr =  vec4 (0,(abs(floor(fu * 8.0))), 0, 1000);
    vec4 vr =  vec4(cr.xyzw * zr.xyzw);




    fragColor = vr.xyzw;
}
