bool dentro(vec2 centro, float radio, vec2 fragCoord)
{
	return distance(fragCoord.xy, centro) < radio;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float radio = 100.4;
	vec2 centro = 0.5*iResolution.xy;

	if( dentro(centro, radio, fragCoord))
	{	
	    fragColor = vec4(0.0, 1.0, 1.0, 1.0);
	}
	else
	{
		fragColor = vec4(1.0, 0.0, 1.0, 1.0);
	}	

}
