// Julia - Traps 1 : https://www.shadertoy.com/view/4d23WG
// Julia - Traps 2 : https://www.shadertoy.com/view/4dfGRn

// line 16: pixel coordinates	
// line 18: c travels around the main cardiod c(t) = (1/2)e^it - (1/4)e^i2t
// line 24: z = zÂ² + c		
// line 25: trap orbit
// line 28: remap	
// line 30: color	

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 z = 1.15*(2.0*fragCoord-iResolution.xy)/iResolution.y;

	vec2 c = 0.51*cos(vec2(0,699)+0.1*iTime) - 
             0.25*cos(vec2(0,699)+0.2*iTime );

	float f = 1e20;
	for( int i=0; i<128; i++ ) 
	{
		z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + c;
		f = min( f, dot(z,z) );
	}

    f = 1.0+log2(f)/16.0;

	fragColor = vec4(f,f*f,f*f*f,1.0);
}
