void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sin_factor = sin(-1.57);
    float cos_factor = cos(-1.57);

    float from = -4.*3.14 ;
    float by = 20.*3.14;
    from = from - float(iFrame)/400. * by;
    vec2 uv = (fragCoord - .5*iResolution.xy)  / iResolution.y * mat2(cos_factor, sin_factor, -sin_factor, cos_factor);
    float a = atan(uv.y, uv.x) / 6.28 + .5 ;
    float x =  from + by*a;
    float y = cos(x)/x;

    vec4 startColor = vec4(0.953, 0.114, 0.071, 1.);
    vec4 finishColor = vec4(0.651, 0.047, 0.376, .5);
    vec4 newColor = startColor - (startColor - finishColor) * a;
    vec4 diff = (newColor - vec4(0.0))*step(0.5, a);
    fragColor = (newColor- diff*(a-0.5)*2.) * step(length(uv), y*0.75 + 0.3) * step(a, float(iFrame)/400.);

}
