#define T(x) texture(iChannel0, (x)/d)
#define C T(T(u-y).zw+u+(y=r.yx*r))/4.

void mainImage(out vec4 c, vec2 u)
{
    vec2 d = iResolution.xy, y = vec2(-1,1), r = y;

    vec4 b = cos(c = C+C+C+C);

    c += .2*(fract((sin(u/d+b.bg)/b.ra).xxyy + c).gbar-c);
}
