void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 0.4 * min(iResolution.x, iResolution.y);
    vec3 blur = 30.0 + 28.0 * sin(iTime * vec3(1, 1.1, 1.2));
    vec2 c = 0.5 * iResolution.xy;
    float d0 = max(0.0, distance(fragCoord, c) - radius);
    vec3 dist = smoothstep(-0.25 * blur, blur, vec3(d0));
    vec2 pt = fragCoord - c;
    float amul = 1.0 + 0.5 * sin(iTime * 0.002) * sin(iTime + atan(pt.y, pt.x));
    float alpha = 1.0 - smoothstep(0.0, amul * max(max(blur.x, blur.y), blur.z), d0);
	fragColor = vec4(
        alpha * dist,
        1.0
    );
}
