void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = fragCoord.xy / iResolution.xy;

	float x = p.x;
	float w = 1.;

	float t = 0.;

	float a = 0.0;

	const float size = 20.;

	for(float i = 0.; i < size; i+=1.0) {
		x *= 20.;
		w = sin(x) * 0.01;

		p.x += i;
		float s = iTime * i / size * 0.5;

		x = (p.x + s) * 2.53;
		w += sin(x) * 0.21 * s * 0.01;

		x = (p.x + s) * 1.53;
		w += sin(x) * 0.41;

		t = (10. - i) / size + w;

		if(p.y < t) {
			a = 1.0 * i / (size - 1.0);
		}
	}



	fragColor = vec4(vec3(a), 1.0);
}
