//abusing many concepts learned from this very website

#define R iResolution
void mainImage( out vec4 c, in vec2 v )
{	
	vec2 T = ( - vec2( .9, .5 ) + v.xy / R.y ) / 20.;
	float F = clamp( abs( sin( 100. * ( sqrt( length (.2*(sin(1e-5*( v.x * v.y ) ) ) * T ) ) - .01 * ( atan( T.y, T.x )+ iTime ) ) ) ), 0. ,1. );
	c = vec4( F ) * clamp( abs( mod( 4.5 * v.x / R.x + vec4( 0., 4., 2., 1. ), 6. ) - 3. ) - 1., 0., v.y );
}
