vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color,float glow) {
    return vec4( 1.0, 1.0, 1.0, clamp( 1.0-(length(pos-uv)-rad)/glow, 0.0, 1.0 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
    vec2 center = iMouse.xy;
	float radius = 50.0;
	float glow = 25.0;

	vec4 layer1 = vec4(rgb(67.0, 125.0, 199.0), 1.0);

	vec3 red = vec3(1.0,1.0,1.0);
	vec4 layer2 = circle(uv, center, radius, red,glow);

	fragColor = mix(layer1, layer2, layer2.a);
}
