// repeats every minute
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;

    vec3  col = vec3( 0.0 );

    for( int i=0; i<256; i++ )
    {
        float an = 6.283185*float(i)/256.0;

        vec2  of = vec2(9.0/16.0,1.0) * vec2(cos(an),sin(an)) * (1.0+0.6*cos(13.0*p.x + 6.0*an + iTime*6.283185/10.0))*0.5 + vec2( 0.0, iTime );
        col = max( col, texture( iChannel0, p + of/30.0 ).zyx );

        of += vec2(15.0,32.0) + vec2( 0.0, iTime );
        col = max( col, texture( iChannel0, p + of/120.0 ).zyx );
    }

    col = pow( col, vec3(1.9,1.4,0.9) ) * pow( 4.0*p.y*(1.0-p.y), 0.25 );

	fragColor = vec4( col, 1.0 );
}
