#define SUBPIXEL_SIZE 2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / SUBPIXEL_SIZE;
    vec2 uv = floor(p)*SUBPIXEL_SIZE / iResolution.xy;

    vec4 color = texture(iChannel0, uv);    
    vec4 result = vec4(0.);

    vec2 remainder = floor(mod(p, 2.0)+0.1);

    if (remainder.x == 1.) {
    	if (remainder.y == 1.) {
            result.g = color.g/sqrt(2.);
        } else {
            result.r = color.r;
        }
    } else {
        if (remainder.y == 1.) {
            result.b = color.b;
        } else {
            result.g = color.g/sqrt(2.);
        }
    }                

	fragColor = result;
}
