//By Seobits
//Creates masking circle and oscillates around the center of the screen.
float tau = 6.2831853071;
float radius = 0.3;
vec4 emptySpaceColor = vec4(0.0, 0.0, 0.0, 0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;
    vec2 offsetScreen = vec2(0.5, 0.5);
   	vec4 tex = texture(iChannel0, uv);

    fragColor = emptySpaceColor;

    offsetScreen.x = 0.5 + cos(iTime) * radius;
    offsetScreen.y = 0.5 + sin(iTime) * radius;
    for(float stepCircle = 0.0; stepCircle <= tau; stepCircle += (tau / 360.0)){
        float xCoord = cos(stepCircle) * radius * aspectRatio;
        float yCoord = sin(stepCircle) * radius;
    	if(uv.x <= offsetScreen.x + xCoord && uv.x >= offsetScreen.x - xCoord){
            if(uv.y <= offsetScreen.y + yCoord && uv.y >= offsetScreen.y - yCoord){
                fragColor = vec4(tex.r, tex.g, tex.b, 1.0);
            }
    	}
    }
}
