void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color=vec4(0.0,0.0,0.0,0.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 colora=vec4(0.0,0.0,0.0,1.0);
    vec4 colorb=vec4(1.0,1.0,1.0,1.0);
    float a=smoothstep(0.0,1.0,uv.y);
    float b=smoothstep(0.0,1.0,a);
    float c=smoothstep(0.0,1.0,b);
    float d=smoothstep(0.0,1.0,c);
    float e=smoothstep(0.0,1.0,d);
    float f=smoothstep(0.0,1.0,e);
    float g=smoothstep(0.0,1.0,f);
    float h=smoothstep(0.0,1.0,g);
    float i=smoothstep(0.0,1.0,h);
    float j=smoothstep(0.0,1.0,i);
    float k=smoothstep(0.0,1.0,j);
    float l=smoothstep(0.0,1.0,k);
    float m=smoothstep(0.0,1.0,l);
    float n=smoothstep(0.0,1.0,m);
    float o=smoothstep(0.0,1.0,n);


   if(uv.x<1.0/16.0)
       color=mix(colora,colorb,uv.y);
   else if(uv.x<2.0/16.0)
   {
       color=mix(colora,colorb,a);}
    else if(uv.x<3.0/16.0)
    {color=mix(colora,colorb,b);}
    else if(uv.x<4.0/16.0)
    {color=mix(colora,colorb,c);}
    else if(uv.x<5.0/16.0)
    {color=mix(colora,colorb,d);}
     else if(uv.x<6.0/16.0)
    {color=mix(colora,colorb,e);}
     else if(uv.x<7.0/16.0)
    {color=mix(colora,colorb,f);}
    else if(uv.x<8.0/16.0)
    {color=mix(colora,colorb,g);}
     else if(uv.x<9.0/16.0)
    {color=mix(colora,colorb,h);}
    else if(uv.x<10.0/16.0)
    {color=mix(colora,colorb,i);}
     else if(uv.x<11.0/16.0)
    {color=mix(colora,colorb,j);}
    else if(uv.x<12.0/16.0)
    {color=mix(colora,colorb,k);}
     else if(uv.x<13.0/16.0)
    {color=mix(colora,colorb,l);}
     else if(uv.x<14.0/16.0)
    {color=mix(colora,colorb,m);}
     else if(uv.x<15.0/16.0)
    {color=mix(colora,colorb,n);}
    else
{color=mix(colora,colorb,o);}
    fragColor=color;
}
