// Prototype for Jonas
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // TODO: Connect to the lamps

	vec2 uv = vec2(-0.5) + fragCoord.xy / iResolution.xy;

	// Zoom in and out
    float zoom = 1.5 + cos(50.0*iTime);
    float radius = 0.1;
    //vec2 r = uv - vec2(0.1*cos(5.0*sin(iTime)*iTime), 0.1*sin(iTime));
    vec2 c = 0.8*vec2(0.1*cos(5.0*sin(iTime)*iTime), 0.1*sin(iTime));
    vec2 r = uv - c;
    vec2 p;
    p.x = cos(4.0*iTime)*r[0] - sin(4.0*iTime)*r[1];
    p.y = sin(4.0*iTime)*r[0] + cos(4.0*iTime)*r[1];
    p.x *= 0.5;
    if (length(p) <= radius * zoom)
		// All colors change
		fragColor = vec4(0.5+0.5*sin(10.0*iTime),0.0,0.0,1.0);
    else
        fragColor = vec4(vec3(0.0), 1.0);        
}
