void mainImage( out vec4 f, in vec2 w )
{
	vec2 u = w.xy / iResolution.xy;

    float e, c, s=sin(iTime), m=32.;
    vec3 d, p;
    p = vec3(.0,9.*s,9.-s);
	d = normalize(vec3(u-.5,0)-p);
    for( s = 0.; s < m;  )
    {
        p += d*e;
		e = length(vec2(length(p.xz)-.2,p.y))-.1;
        c = .7-s++/m;
        f.g += sin(e)*c*c*c;
        if( e < .01 )
        {
  			f = vec4(c,c*.4,c*.4,1); 
            s=m;
        }        
    }
}
