// Based on code in Texturing and Modeling: A Procedural Approach (3rd Edition)
// Made much, much shorter and simpler by FabriceNeyret2 :)

#define P(a) vec3(cos(a), sin(a), 0)

void
mainImage(out vec4 O, vec2 U)
{
    float r = length(U += U - (O.xy = iResolution.xy)) / O.y,
          a = fract(atan(U.x, U.y) / 1.26 - smoothstep(.2, .8, fract(.5 * iTime)));
    vec3 v = .28 * P(.63),
         w =  r  * P(min(a, 1.-a));
    v.x -= .8;
    w.x -= .8;
    O = vec4(1, smoothstep(0., 2./O.y, cross(v,w).z), 0, 1);
}
