void mainImage( out vec4 fragColor, in vec2 fragCoord )



{
	vec2 uv = fragCoord.xy / iResolution.xy-.1;
    uv.x*=iResolution.x+.1/iResolution.y;

    vec2 ux = fragCoord.xy / iResolution.xy-.1;
    ux.x*=iResolution.y+.1/iResolution.x;


    //fragColor = vec4(1,uv+.2,0);
    fragColor = vec4(ux*sin(iTime)+.5,uv*cos(iTime)+.5);


}
