float seed=0.;
float hash(float x)
{
    return fract(seed+sin(x*556.4556+123.3*seed)*453.54);
}
float cubic(float x,float a,float da,float b, float db)
{
    vec4 abcd = vec4(2.*a-2.*b+da+db,-3.*a+3.*b-2.*da-db,da,a);
    return x*x*x*abcd.x+x*x*abcd.y+x*abcd.z+abcd.w;
}

float cubicnoise1(float t)
{
    float T=floor(t);
    float a = hash(T++);
    float b = hash(T++);
    float c = hash(T++);
    float d = hash(T);
    float db =(c-a)*.5;
    float dc =(d-b)*.5;
    return cubic(fract(t),b,db,c,dc)*2.-1.;
}
float fbm1(float t)
{
    float n =cubicnoise1(t);
    n+=cubicnoise1(2.*t)/2.;
    n+=cubicnoise1(4.*t)/4.;
    n+=cubicnoise1(8.*t)/8.;
    return n;
}
vec2 Ccubn(float t)
{
    t*=.2;
    seed++;
    return vec2(fbm1(t),fbm1(.5-t));
}

