void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	const float pi = 3.1415926535;
    vec2 p = fragCoord.xy - iResolution.xy / 2.0;
    float phi = atan(p.y, p.x + 1e-6);

    float fin = mod(floor(phi * 3.0 / pi + 0.5), 6.0);
    float phi_fin = fin * pi / 3.0;

    vec2 dir = vec2(cos(phi_fin), sin(phi_fin));
    float l = dot(dir, p) - iTime * iResolution.y / 5.0;

    float ivr = 20.0;
    float seg = l / ivr;

    float w = sin(floor(seg) * 0.2 - iTime) * 0.4 + 0.5;
    float c = (w / 2.0 - abs(fract(seg) - 0.5)) * ivr;

    fragColor = vec4(c, c, c, 1);
}
