// Sunflower Transform Mosaic Floor - written 2015 by JT
// (a modification of https://www.shadertoy.com/view/Mdd3R7
//  resp. https://www.shadertoy.com/view/Xdd3Dr
//  visualized using https://www.shadertoy.com/view/Xd33W8)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

vec4 C(vec2 I)
{
    I = vec2(0, length(I)) + atan(I.y, I.x) / 6.283;
    I.x = ceil(I.y) - I.x;
    I.x *= I.x * 2.47;

    vec4 O;
    O -= O + smoothstep(.4, .5, length(fract(I)-.5)) - 1.;
    I.y = 0.;
    O *= texture(iChannel0, ceil(I)*.01); // NOTE: filter set to linear instead of mipmap
    return O;
}

// Thanks to FabriceNeyret2 for sunflower-optimizations
void mainImage( out vec4 O, in vec2 I )
{
	vec2 R = iResolution.xy;
    I = (I+I-R)/R.y;
    float d = step(I.y,.0);
    I /= vec2(2., I.y) * abs(I.y);
    d /= I.y*I.y;
    //I.x += iTime;

    float t = iTime*.1;
    float c = cos(t);
    float s = sin(t);
    //I *= .5 + 10.*(.5-.5*cos(t)); // higher view but moiree-patterns
    I *= .5 + 5.*(.5-.5*cos(t)); // avoid most moiree-patterns
    vec2 cs = vec2(c, s);
    vec2 sc = vec2(-s, c);
    I = -cs * I.y - sc * I.x;
    I += 10. * cs + sc;
    if(length(iMouse) > 0.) // show center if mouse not used
    	I -= 20.*iMouse.xy/iResolution.xy-10.;
    O = d*C(I*2.) + (1.-d)*vec4(.8,.9,1,1);
}
