#define t iTime * .6
#define FOV 9.
#define RES 20.
#define LAYERS 8.
#define n (u / iResolution.y - .5)

vec2 hash( vec2 p ) {                       // rand in [-1,1]
    p = vec2( dot(p,vec2(127.1,311.7)),
              dot(p,vec2(269.5,183.3)) );
    return -1. + 2.*fract(sin(p+20.)*53758.5453123);
}

float noise( vec2 p ) {
    vec2 i = floor((p)), f = fract((p));
    vec2 u = f*f*(3.-2.*f);
    return mix( mix( dot( hash( i + vec2(0.,0.) ), f - vec2(0.,0.) ), 
                     dot( hash( i + vec2(1.,0.) ), f - vec2(1.,0.) ), u.x),
                mix( dot( hash( i + vec2(0.,1.) ), f - vec2(0.,1.) ), 
                     dot( hash( i + vec2(1.,1.) ), f - vec2(1.,1.) ), u.x), u.y);
}

void mainImage( out vec4 c, in vec2 u )
{
	float d, s;

    c = vec4(.4, sin(t / 4.), cos(t / 7.), 1.) * cos(n.y * 3.14);

    for (float i = 1.; i < LAYERS; i++) {
        vec2 uv = n * tan(radians(i * 4. + FOV) / 2.0);
        uv += vec2(- t / 8. + i / 12., sin(i - t / 4. + uv.x) * .1);

        s = .01 * noise(ceil(uv * RES + i * 9.) + i + t * vec2(.2, .01));
        d = length(mod(uv, 1. / RES) - .5 / RES);

        if (d < s) c += 1. / i - d * i * 6.; 
    }

}
