vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void render( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 coord = (fragCoord.xy - iResolution.xy / 2.0) / iResolution.y * 2.0;
    coord += 0.02 * sin(iTime / 50.0) * sin(fragCoord.y / 10.0 + iTime);

    float dist = length(coord);

    vec2 cur = coord;

	for (int i = 0; i < 15; ++i)
    {
        cur = vec2(cur.x * cur.x - cur.y * cur.y, (2.0 + 0.5 * sin(iTime)) * cur.x * cur.y) + 
            coord * sin(iTime) + 0.5 * sin(iTime * 2.5);
    }

    float hue = sin(dist + iTime) / 2.0 + 0.5;
    vec3 color = hsv2rgb(vec3(hue, 1.0, 1.0)) + dist * length(cur) / 1.5;

    fragColor = vec4(length(cur) * color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color;
    for (float x = -2.0; x <= 2.0; ++x)
    {
        for (float y = -2.0; y <= 2.0; ++y)
        {
            vec4 sampleColor;
            vec2 sampleCoord = fragCoord + vec2(x, y);
            render(sampleColor, sampleCoord);
            color += sampleColor * (pow(y, 5.0) * 6.0 + 1.0);
        }
    }

    color /= 25.0;

    fragColor = color;
}
