// 258 char version (color and double rotation)

#define M(c) length(max(abs(b)-c,0.))

void mainImage(out vec4 O, vec2 p) {
    float  C  = iTime, a=sin(C), d=cos(C);
    mat3   u  = mat3(a,d*d,d*a,0,a,-d,-d,a*d,a*a);
    vec3   b  = vec3(0,0,7)*u,
           e  = .6*(.5-vec3(p/iResolution.y,2.))*u;
    for(int i=0;i<99;i++) b+=e*max(.12-M(.9),a=M(1.));
    O.rgb = a<1.?e*e:e-e;}



